import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertScholarshipApplicationSchema, insertWhatsappSubscriptionSchema, insertChatMessageSchema } from "@shared/schema";
import OpenAI from "openai";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export async function registerRoutes(app: Express): Promise<Server> {

  // Scholarship eligibility endpoint
  app.post("/api/check-eligibility", async (req, res) => {
    try {
      const data = insertScholarshipApplicationSchema.parse(req.body);

      // Mock scholarship matching logic
      const scholarships = [];

      if (data.educationLevel === "pre-matric") {
        scholarships.push({
          name: "Pre-Matric Scholarship for SC/ST Students",
          amount: "‚Çπ1,200-‚Çπ12,000",
          deadline: "2024-03-31",
          eligible: ["sc", "st"].includes(data.casteCategory.toLowerCase())
        });
      }

      if (data.educationLevel === "post-matric") {
        scholarships.push({
          name: "Post-Matric Scholarship for SC/ST Students", 
          amount: "‚Çπ15,000-‚Çπ85,000",
          deadline: "2024-04-30",
          eligible: ["sc", "st"].includes(data.casteCategory.toLowerCase())
        });
      }

      if (data.incomeLevel === "below-2-lakh") {
        scholarships.push({
          name: "Merit-cum-Means Scholarship",
          amount: "‚Çπ20,000-‚Çπ50,000", 
          deadline: "2024-05-15",
          eligible: true
        });
      }

      const eligibleScholarships = scholarships.filter(s => s.eligible);

      const application = await storage.createScholarshipApplication({
        ...data,
        eligibleScholarships: eligibleScholarships
      });

      res.json({ success: true, scholarships: eligibleScholarships, applicationId: application.id });
    } catch (error) {
      res.status(400).json({ error: "Invalid data provided" });
    }
  });

  // WhatsApp subscription endpoint
  app.post("/api/subscribe-whatsapp", async (req, res) => {
    try {
      const data = insertWhatsappSubscriptionSchema.parse(req.body);

      // Check if already subscribed
      const existing = await storage.getWhatsappSubscription(data.phoneNumber);
      if (existing) {
        return res.json({ success: true, message: "Already subscribed" });
      }

      const subscription = await storage.createWhatsappSubscription(data);
      res.json({ success: true, subscription });
    } catch (error) {
      res.status(400).json({ error: "Invalid phone number" });
    }
  });

  // Help centers endpoint
  app.get("/api/help-centers", async (req, res) => {
    try {
      const { pincode, type } = req.query;
      const centers = await storage.getHelpCenters(
        pincode as string, 
        type as string
      );
      res.json(centers);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch help centers" });
    }
  });

  // Chat with AI assistant
  app.post("/api/chat", async (req, res) => {
    try {
      const data = insertChatMessageSchema.parse(req.body);

      // Get AI response from OpenAI
      // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      const completion = await openai.chat.completions.create({
        model: "gpt-5",
        messages: [
          {
            role: "system",
            content: "You are a helpful scholarship assistant for Indian students. Help them with Aadhaar seeding, DBT processes, and scholarship applications. Be concise and practical."
          },
          {
            role: "user", 
            content: data.message
          }
        ],
        response_format: { type: "json_object" }
      });

      const aiResponse = JSON.parse(completion.choices[0].message.content || '{"response": "I apologize, but I could not process your request."}');

      const chatMessage = await storage.createChatMessage({
        ...data,
        response: aiResponse.response
      });

      res.json({ response: aiResponse.response, messageId: chatMessage.id });
    } catch (error) {
      res.status(500).json({ error: "Failed to get AI response" });
    }
  });

  // Gamification endpoints
  app.post("/api/quiz-score", async (req, res) => {
    try {
      const { userId, score, quizType } = req.body;

      const existing = await storage.getGamificationProgress(userId);
      const newQuizScores = existing?.quizScores ? 
        { ...existing.quizScores, [quizType]: score } : 
        { [quizType]: score };

      const newBadges = [];
      if (score >= 80) newBadges.push(`${quizType}_expert`);
      if (score >= 60) newBadges.push(`${quizType}_proficient`);

      const progress = await storage.updateGamificationProgress(userId, {
        quizScores: newQuizScores,
        badges: newBadges,
        totalPoints: (existing?.totalPoints || 0) + score
      });

      res.json({ success: true, progress });
    } catch (error) {
      res.status(500).json({ error: "Failed to update progress" });
    }
  });

  app.get("/api/gamification/:userId", async (req, res) => {
    try {
      const progress = await storage.getGamificationProgress(req.params.userId);
      res.json(progress || { level: 1, totalPoints: 0, badges: [], quizScores: {} });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch progress" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type ScholarshipApplication,
  type InsertScholarshipApplication,
  type WhatsappSubscription,
  type InsertWhatsappSubscription,
  type HelpCenter,
  type GamificationProgress,
  type ChatMessage,
  type InsertChatMessage
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  createScholarshipApplication(app: InsertScholarshipApplication): Promise<ScholarshipApplication>;
  getScholarshipApplicationsByUser(userId: string): Promise<ScholarshipApplication[]>;

  createWhatsappSubscription(sub: InsertWhatsappSubscription): Promise<WhatsappSubscription>;
  getWhatsappSubscription(phoneNumber: string): Promise<WhatsappSubscription | undefined>;

  getHelpCenters(pincode?: string, type?: string): Promise<HelpCenter[]>;

  getGamificationProgress(userId: string): Promise<GamificationProgress | undefined>;
  updateGamificationProgress(userId: string, progress: Partial<GamificationProgress>): Promise<GamificationProgress>;

  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  getChatHistory(userId: string): Promise<ChatMessage[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private scholarshipApplications: Map<string, ScholarshipApplication>;
  private whatsappSubscriptions: Map<string, WhatsappSubscription>;
  private helpCenters: Map<string, HelpCenter>;
  private gamificationProgress: Map<string, GamificationProgress>;
  private chatMessages: Map<string, ChatMessage>;

  constructor() {
    this.users = new Map();
    this.scholarshipApplications = new Map();
    this.whatsappSubscriptions = new Map();
    this.helpCenters = new Map();
    this.gamificationProgress = new Map();
    this.chatMessages = new Map();

    // Initialize some help centers data
    this.initializeHelpCenters();
  }

  private initializeHelpCenters() {
    const centers: HelpCenter[] = [
      {
        id: randomUUID(),
        name: "State Bank of India - Main Branch",
        type: "bank",
        address: "Gandhi Maidan, Patna, Bihar 800001",
        phoneNumber: "0612-2234567",
        latitude: "25.6093",
        longitude: "85.1376",
        pincode: "800001",
        services: ["aadhaar_seeding", "dbt_verification", "account_opening"]
      },
      {
        id: randomUUID(),
        name: "Common Service Center",
        type: "csc",
        address: "Boring Road, Patna, Bihar 800013",
        phoneNumber: "9876543210",
        latitude: "25.5941",
        longitude: "85.1376",
        pincode: "800013",
        services: ["aadhaar_seeding", "digital_services"]
      },
      {
        id: randomUUID(),
        name: "UIDAI Enrollment Center",
        type: "uidai",
        address: "Kankarbagh, Patna, Bihar 800020",
        phoneNumber: "0612-2345678",
        latitude: "25.5744",
        longitude: "85.0960",
        pincode: "800020",
        services: ["aadhaar_enrollment", "aadhaar_update"]
      }
    ];

    centers.forEach(center => {
      this.helpCenters.set(center.id, center);
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createScholarshipApplication(app: InsertScholarshipApplication): Promise<ScholarshipApplication> {
    const id = randomUUID();
    const application: ScholarshipApplication = { 
      ...app, 
      id, 
      userId: app.userId || null,
      createdAt: new Date() 
    };
    this.scholarshipApplications.set(id, application);
    return application;
  }

  async getScholarshipApplicationsByUser(userId: string): Promise<ScholarshipApplication[]> {
    return Array.from(this.scholarshipApplications.values()).filter(
      (app) => app.userId === userId
    );
  }

  async createWhatsappSubscription(sub: InsertWhatsappSubscription): Promise<WhatsappSubscription> {
    const id = randomUUID();
    const subscription: WhatsappSubscription = { 
      ...sub, 
      id, 
      isActive: sub.isActive ?? true,
      createdAt: new Date() 
    };
    this.whatsappSubscriptions.set(id, subscription);
    return subscription;
  }

  async getWhatsappSubscription(phoneNumber: string): Promise<WhatsappSubscription | undefined> {
    return Array.from(this.whatsappSubscriptions.values()).find(
      (sub) => sub.phoneNumber === phoneNumber
    );
  }

  async getHelpCenters(pincode?: string, type?: string): Promise<HelpCenter[]> {
    let centers = Array.from(this.helpCenters.values());

    if (pincode) {
      centers = centers.filter(center => center.pincode === pincode);
    }

    if (type) {
      centers = centers.filter(center => center.type === type);
    }

    return centers;
  }

  async getGamificationProgress(userId: string): Promise<GamificationProgress | undefined> {
    return Array.from(this.gamificationProgress.values()).find(
      (progress) => progress.userId === userId
    );
  }

  async updateGamificationProgress(userId: string, progressUpdate: Partial<GamificationProgress>): Promise<GamificationProgress> {
    const existing = await this.getGamificationProgress(userId);

    const progress: GamificationProgress = existing ? 
      { ...existing, ...progressUpdate } : 
      { 
        id: randomUUID(), 
        userId, 
        quizScores: null,
        badges: null,
        level: 1,
        totalPoints: 0,
        ...progressUpdate 
      };

    this.gamificationProgress.set(progress.id, progress);
    return progress;
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const id = randomUUID();
    const chatMessage: ChatMessage = { 
      ...message, 
      id, 
      userId: message.userId || null,
      response: message.response || null,
      timestamp: new Date() 
    };
    this.chatMessages.set(id, chatMessage);
    return chatMessage;
  }

  async getChatHistory(userId: string): Promise<ChatMessage[]> {
    return Array.from(this.chatMessages.values())
      .filter((msg) => msg.userId === userId)
      .sort((a, b) => (a.timestamp?.getTime() || 0) - (b.timestamp?.getTime() || 0));
  }
}

export const storage = new MemStorage();

