@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0 0% 100%);
  --foreground: hsl(222.2 84% 4.9%);
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(222.2 84% 4.9%);
  --primary: hsl(220 91% 28%); /* Enhanced government blue */
  --primary-foreground: hsl(210 40% 98%);
  --secondary: hsl(142 76% 36%); /* Indian flag green */
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(210 40% 96%);
  --muted-foreground: hsl(215.4 16.3% 46.9%);
  --accent: hsl(30 100% 55%); /* Enhanced saffron */
  --accent-foreground: hsl(222.2 84% 4.9%);
  --destructive: hsl(0 84.2% 60.2%);
  --destructive-foreground: hsl(210 40% 98%);
  --border: hsl(214.3 31.8% 91.4%);
  --input: hsl(214.3 31.8% 91.4%);
  --ring: hsl(221.2 83.2% 53.3%);
  --radius: 8px;
  --font-sans: 'Inter', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;

  /* Gradient variables */
  --gradient-primary: linear-gradient(135deg, hsl(220 91% 28%) 0%, hsl(30 100% 55%) 50%, hsl(142 76% 36%) 100%);
  --gradient-secondary: linear-gradient(45deg, hsl(220 91% 28%) 0%, hsl(142 76% 36%) 100%);
  --gradient-accent: linear-gradient(90deg, hsl(30 100% 55%) 0%, hsl(220 91% 28%) 100%);
}

.dark {
  --background: hsl(220 30% 8%);
  --foreground: hsl(210 40% 98%);
  --card: hsl(220 30% 10%);
  --card-foreground: hsl(210 40% 98%);
  --primary: hsl(220 91% 35%);
  --primary-foreground: hsl(220 30% 8%);
  --secondary: hsl(142 76% 40%);
  --secondary-foreground: hsl(220 30% 8%);
  --muted: hsl(217.2 32.6% 17.5%);
  --muted-foreground: hsl(215 20.2% 65.1%);
  --accent: hsl(30 100% 65%);
  --accent-foreground: hsl(220 30% 8%);
  --destructive: hsl(0 62.8% 30.6%);
  --destructive-foreground: hsl(210 40% 98%);
  --border: hsl(217.2 32.6% 17.5%);
  --input: hsl(217.2 32.6% 17.5%);
  --ring: hsl(212.7 26.8% 83.9%);

  /* Dark mode gradients */
  --gradient-primary: linear-gradient(135deg, hsl(220 91% 35%) 0%, hsl(30 100% 65%) 50%, hsl(142 76% 40%) 100%);
  --gradient-secondary: linear-gradient(45deg, hsl(220 91% 35%) 0%, hsl(142 76% 40%) 100%);
  --gradient-accent: linear-gradient(90deg, hsl(30 100% 65%) 0%, hsl(220 91% 35%) 100%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased text-foreground;
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--muted)) 50%, 
      hsl(var(--background)) 100%);
    min-height: 100vh;
  }
}

/* Custom animations */
.animate-bounce-slow {
  animation: bounce 2s infinite;
}

.gradient-text {
  background: var(--gradient-secondary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-weight: 600;
}

.gradient-bg {
  background: var(--gradient-primary);
}

.gradient-bg-secondary {
  background: var(--gradient-secondary);
}

.gradient-bg-accent {
  background: var(--gradient-accent);
}

.glass-effect {
  backdrop-filter: blur(10px);
  background: rgba(255, 255, 255, 0.9);
}

.progress-step.active {
  background: hsl(220 91% 23%);
  color: white;
}

.progress-step.completed {
  background: hsl(142 76% 36%);
  color: white;
}

/* Voice assistant styles */
.voice-recording {
  animation: pulse 1.5s infinite;
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

/* Map styles */
.map-marker {
  cursor: pointer;
  transition: transform 0.2s;
}

.map-marker:hover {
  transform: scale(1.1);
}

/* Chat bubble styles */
.chat-bubble {
  max-width: 80%;
  word-wrap: break-word;
}

.chat-bubble.user {
  margin-left: auto;
  background: var(--gradient-secondary);
  color: hsl(210 40% 98%);
}

.chat-bubble.assistant {
  margin-right: auto;
  background: hsl(210 40% 96%);
  color: hsl(222.2 84% 4.9%);
}

/* Enhanced animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes shimmer {
  0% { background-position: -200px 0; }
  100% { background-position: calc(200px + 100%) 0; }
}

.animate-fadeInUp {
  animation: fadeInUp 0.6s ease-out;
}

.animate-slideInRight {
  animation: slideInRight 0.5s ease-out;
}

.shimmer {
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
  background-size: 200px 100%;
  animation: shimmer 1.5s infinite;
}

/* Enhanced card effects */
.card-hover {
  transition: all 0.3s ease;
  border: 1px solid hsl(var(--border));
}

.card-hover:hover {
  transform: translateY(-4px);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  border-color: hsl(var(--primary));
}

/* Progress indicators */
.progress-glow {
  box-shadow: 0 0 10px hsl(var(--primary));
}

/* Language selector styles */
.language-flag {
  width: 20px;
  height: 15px;
  border-radius: 2px;
  display: inline-block;
  margin-right: 8px;
}

