import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Calendar, Clock, Bell, Download } from "lucide-react";
import { type Language, useTranslation } from "@/lib/i18n";

interface CountdownTrackerProps {
  isOpen: boolean;
  onClose: () => void;
  language: Language;
}

interface ScholarshipTimeline {
  id: string;
  name: string;
  phases: {
    name: string;
    date: string;
    status: 'completed' | 'active' | 'upcoming';
    description: string;
  }[];
}

const scholarshipTimelines: ScholarshipTimeline[] = [
  {
    id: "pre-matric-sc-st",
    name: "Pre-Matric Scholarship for SC/ST Students",
    phases: [
      {
        name: "Application Period",
        date: "2024-01-15 to 2024-03-31",
        status: "completed",
        description: "Online application submission period"
      },
      {
        name: "Document Verification",
        date: "2024-04-01 to 2024-04-30",
        status: "active",
        description: "Verification of submitted documents by authorities"
      },
      {
        name: "Approval & Sanction",
        date: "2024-05-01 to 2024-05-31",
        status: "upcoming",
        description: "Final approval and scholarship amount sanctioning"
      },
      {
        name: "Fund Disbursement",
        date: "2024-06-01 to 2024-06-15",
        status: "upcoming",
        description: "Direct transfer to DBT-enabled bank accounts"
      }
    ]
  },
  {
    id: "post-matric-obc",
    name: "Post-Matric Scholarship for OBC Students",
    phases: [
      {
        name: "Application Period",
        date: "2024-02-01 to 2024-04-30",
        status: "active",
        description: "Online application submission period"
      },
      {
        name: "Document Verification",
        date: "2024-05-01 to 2024-05-31",
        status: "upcoming",
        description: "Verification of submitted documents by authorities"
      },
      {
        name: "Merit List Preparation",
        date: "2024-06-01 to 2024-06-15",
        status: "upcoming",
        description: "Preparation of merit-based selection list"
      },
      {
        name: "Fund Disbursement",
        date: "2024-07-01 to 2024-07-15",
        status: "upcoming",
        description: "Direct transfer to DBT-enabled bank accounts"
      }
    ]
  }
];

export default function CountdownTracker({ isOpen, onClose, language }: CountdownTrackerProps) {
  const [selectedTimeline, setSelectedTimeline] = useState(scholarshipTimelines[0]);
  const [timeRemaining, setTimeRemaining] = useState<Record<string, string>>({});
  const t = useTranslation(language);

  useEffect(() => {
    const updateCountdown = () => {
      const newTimeRemaining: Record<string, string> = {};

      scholarshipTimelines.forEach(timeline => {
        timeline.phases.forEach(phase => {
          if (phase.status === 'active' || phase.status === 'upcoming') {
            const endDate = new Date(phase.date.split(' to ')[1] || phase.date);
            const now = new Date();
            const difference = endDate.getTime() - now.getTime();

            if (difference > 0) {
              const days = Math.floor(difference / (1000 * 60 * 60 * 24));
              const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
              const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));

              newTimeRemaining[`${timeline.id}-${phase.name}`] = `${days}d ${hours}h ${minutes}m`;
            } else {
              newTimeRemaining[`${timeline.id}-${phase.name}`] = "Expired";
            }
          }
        });
      });

      setTimeRemaining(newTimeRemaining);
    };

    updateCountdown();
    const interval = setInterval(updateCountdown, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'text-green-600 bg-green-100';
      case 'active':
        return 'text-blue-600 bg-blue-100';
      case 'upcoming':
        return 'text-gray-600 bg-gray-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return '‚úÖ';
      case 'active':
        return 'üîÑ';
      case 'upcoming':
        return '‚è≥';
      default:
        return 'üìÖ';
    }
  };

  const generateCalendarEvent = (phase: any) => {
    const startDate = new Date(phase.date.split(' to ')[0]);
    const endDate = new Date(phase.date.split(' to ')[1] || phase.date);

    const calendarEvent = {
      title: `${selectedTimeline.name} - ${phase.name}`,
      start: startDate.toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z',
      end: endDate.toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z',
      description: phase.description
    };

    const googleCalendarUrl = `https://calendar.google.com/calendar/render?action=TEMPLATE&text=${encodeURIComponent(calendarEvent.title)}&dates=${calendarEvent.start}/${calendarEvent.end}&details=${encodeURIComponent(calendarEvent.description)}`;

    window.open(googleCalendarUrl, '_blank');
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="countdown-tracker">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Calendar className="text-primary" />
            Scholarship Timeline Tracker
          </DialogTitle>
        </DialogHeader>

        <div className="grid lg:grid-cols-3 gap-6">
          {/* Scholarship Selection */}
          <div className="space-y-4">
            <h3 className="font-semibold">Select Scholarship</h3>
            {scholarshipTimelines.map((timeline) => (
              <Card 
                key={timeline.id}
                className={`cursor-pointer transition-colors ${
                  selectedTimeline.id === timeline.id ? 'border-primary bg-primary/5' : 'hover:bg-muted'
                }`}
                onClick={() => setSelectedTimeline(timeline)}
                data-testid={`timeline-${timeline.id}`}
              >
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm">{timeline.name}</CardTitle>
                </CardHeader>
                <CardContent className="pt-0">
                  <div className="flex items-center gap-2 text-xs text-muted-foreground">
                    <Clock size={12} />
                    <span>{timeline.phases.length} phases</span>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Timeline Display */}
          <div className="lg:col-span-2 space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="font-semibold">{selectedTimeline.name}</h3>
              <Button
                size="sm"
                variant="outline"
                onClick={() => generateCalendarEvent(selectedTimeline.phases.find(p => p.status === 'active' || p.status === 'upcoming'))}
                data-testid="add-to-calendar-button"
              >
                <Calendar className="mr-2" size={14} />
                Add to Calendar
              </Button>
            </div>

            <div className="space-y-4">
              {selectedTimeline.phases.map((phase, index) => (
                <Card key={index} className="relative">
                  <CardHeader className="pb-2">
                    <CardTitle className="flex items-center justify-between text-sm">
                      <div className="flex items-center gap-2">
                        <span className="text-lg">{getStatusIcon(phase.status)}</span>
                        <span>{phase.name}</span>
                      </div>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(phase.status)}`}>
                        {phase.status.charAt(0).toUpperCase() + phase.status.slice(1)}
                      </span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <Calendar size={14} />
                      <span>{phase.date}</span>
                    </div>

                    <p className="text-sm">{phase.description}</p>

                    {(phase.status === 'active' || phase.status === 'upcoming') && 
                     timeRemaining[`${selectedTimeline.id}-${phase.name}`] && (
                      <div className="bg-accent/10 p-3 rounded-lg">
                        <div className="flex items-center gap-2 text-sm font-medium text-accent">
                          <Clock size={14} />
                          <span>
                            {phase.status === 'active' ? 'Time Remaining: ' : 'Starts in: '}
                            {timeRemaining[`${selectedTimeline.id}-${phase.name}`]}
                          </span>
                        </div>
                      </div>
                    )}

                    {phase.status === 'active' && (
                      <div className="flex gap-2">
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => generateCalendarEvent(phase)}
                          data-testid="set-reminder-button"
                        >
                          <Bell className="mr-2" size={12} />
                          Set Reminder
                        </Button>
                      </div>
                    )}
                  </CardContent>

                  {/* Progress Line */}
                  {index < selectedTimeline.phases.length - 1 && (
                    <div className="absolute left-6 top-full w-0.5 h-4 bg-border"></div>
                  )}
                </Card>
              ))}
            </div>

            {/* Quick Actions */}
            <Card className="bg-muted">
              <CardContent className="pt-6">
                <h4 className="font-semibold mb-3">Quick Actions</h4>
                <div className="grid md:grid-cols-2 gap-3">
                  <Button variant="outline" size="sm" data-testid="download-timeline-button">
                    <Download className="mr-2" size={14} />
                    Download Timeline
                  </Button>
                  <Button variant="outline" size="sm" data-testid="check-status-button">
                    <Clock className="mr-2" size={14} />
                    Check Status
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/dbt-simulator.tsx.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { CheckCircle, XCircle, AlertCircle, ArrowRight } from "lucide-react";
import { type Language } from "@/lib/i18n";

interface DBTSimulatorProps {
  isOpen: boolean;
  onClose: () => void;
  language: Language;
}

interface SimulationStep {
  id: number;
  title: string;
  description: string;
  status: 'pending' | 'success' | 'error' | 'warning';
}

export default function DBTSimulator({ isOpen, onClose, language }: DBTSimulatorProps) {
  const [currentStep, setCurrentStep] = useState(0);
  const [aadhaarNumber, setAadhaarNumber] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [isSimulationStarted, setIsSimulationStarted] = useState(false);
  const [simulationSteps, setSimulationSteps] = useState<SimulationStep[]>([]);

  const startSimulation = () => {
    if (!aadhaarNumber || !accountNumber) return;

    const steps: SimulationStep[] = [
      {
        id: 1,
        title: "Connecting to UIDAI Database",
        description: "Verifying Aadhaar number authenticity",
        status: 'pending'
      },
      {
        id: 2,
        title: "Bank Account Verification",
        description: "Checking account details and KYC status",
        status: 'pending'
      },
      {
        id: 3,
        title: "Aadhaar-Account Linkage Check",
        description: "Verifying if Aadhaar is linked to bank account",
        status: 'pending'
      },
      {
        id: 4,
        title: "DBT Enablement Status",
        description: "Checking if account is DBT-enabled for transfers",
        status: 'pending'
      },
      {
        id: 5,
        title: "Final Verification",
        description: "Confirming eligibility for scholarship transfers",
        status: 'pending'
      }
    ];

    setSimulationSteps(steps);
    setIsSimulationStarted(true);
    setCurrentStep(0);

    // Simulate step-by-step verification
    simulateSteps(steps);
  };

  const simulateSteps = (steps: SimulationStep[]) => {
    steps.forEach((step, index) => {
      setTimeout(() => {
        setCurrentStep(index + 1);
        setSimulationSteps(prevSteps => 
          prevSteps.map(s => 
            s.id === step.id 
              ? { ...s, status: getStepStatus(index) }
              : s
          )
        );
      }, (index + 1) * 2000);
    });
  };

  const getStepStatus = (index: number): 'success' | 'error' | 'warning' => {
    // Simulate different scenarios
    if (aadhaarNumber.includes('1234')) {
      if (index === 2) return 'error'; // Account not linked
      if (index === 3) return 'warning'; // Partial linkage
    }
    return 'success';
  };

  const getStatusIcon = (status: 'pending' | 'success' | 'error' | 'warning') => {
    switch (status) {
      case 'success':
        return <CheckCircle className="text-green-500" size={20} />;
      case 'error':
        return <XCircle className="text-red-500" size={20} />;
      case 'warning':
        return <AlertCircle className="text-yellow-500" size={20} />;
      default:
        return <div className="w-5 h-5 border-2 border-gray-300 rounded-full animate-spin" />;
    }
  };

  const resetSimulation = () => {
    setIsSimulationStarted(false);
    setCurrentStep(0);
    setSimulationSteps([]);
    setAadhaarNumber("");
    setAccountNumber("");
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto" data-testid="dbt-simulator">
        <DialogHeader>
          <DialogTitle>DBT Seeding Status Simulator</DialogTitle>
        </DialogHeader>

        {!isSimulationStarted ? (
          <div className="space-y-6">
            <div className="bg-muted p-4 rounded-lg">
              <h3 className="font-semibold mb-2">How this simulator works:</h3>
              <ul className="text-sm text-muted-foreground space-y-1">
                <li>‚Ä¢ Enter your Aadhaar and bank account details</li>
                <li>‚Ä¢ Experience a realistic verification process</li>
                <li>‚Ä¢ Understand each step of DBT seeding</li>
                <li>‚Ä¢ Learn what to expect on real portals</li>
              </ul>
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="aadhaar">Aadhaar Number</Label>
                <Input
                  id="aadhaar"
                  value={aadhaarNumber}
                  onChange={(e) => setAadhaarNumber(e.target.value)}
                  placeholder="XXXX XXXX XXXX"
                  maxLength={12}
                  data-testid="aadhaar-input"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Use 1234 in number for demo error scenario
                </p>
              </div>

              <div>
                <Label htmlFor="account">Bank Account Number</Label>
                <Input
                  id="account"
                  value={accountNumber}
                  onChange={(e) => setAccountNumber(e.target.value)}
                  placeholder="Account Number"
                  data-testid="account-input"
                />
              </div>
            </div>

            <Button 
              onClick={startSimulation}
              disabled={!aadhaarNumber || !accountNumber}
              className="w-full"
              data-testid="start-simulation-button"
            >
              Start DBT Status Check
              <ArrowRight className="ml-2" size={16} />
            </Button>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="bg-primary/10 p-4 rounded-lg">
              <h3 className="font-semibold">Simulation in Progress</h3>
              <p className="text-sm text-muted-foreground">
                This is how the verification process works on actual government portals
              </p>
            </div>

            <div className="space-y-3">
              {simulationSteps.map((step, index) => (
                <Card key={step.id} className={currentStep >= step.id ? "border-primary" : ""}>
                  <CardHeader className="pb-2">
                    <CardTitle className="flex items-center justify-between text-sm">
                      <span>Step {step.id}: {step.title}</span>
                      {getStatusIcon(step.status)}
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <p className="text-sm text-muted-foreground">{step.description}</p>

                    {step.status === 'error' && (
                      <div className="mt-2 p-2 bg-red-50 border border-red-200 rounded text-sm text-red-700">
                        ‚ùå Error: Aadhaar not linked to this account. Please visit your bank branch.
                      </div>
                    )}

                    {step.status === 'warning' && (
                      <div className="mt-2 p-2 bg-yellow-50 border border-yellow-200 rounded text-sm text-yellow-700">
                        ‚ö†Ô∏è Warning: Partial linkage detected. Additional verification may be required.
                      </div>
                    )}

                    {step.status === 'success' && (
                      <div className="mt-2 p-2 bg-green-50 border border-green-200 rounded text-sm text-green-700">
                        ‚úÖ Success: Verification completed successfully.
                      </div>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>

            {currentStep >= simulationSteps.length && (
              <div className="mt-6 space-y-4">
                <Card className="bg-secondary/10">
                  <CardContent className="pt-6">
                    <h3 className="font-semibold mb-2">Simulation Complete!</h3>
                    <p className="text-sm text-muted-foreground mb-4">
                      You've experienced the DBT verification process. In real scenarios, 
                      results may vary based on your actual account status.
                    </p>

                    <div className="flex gap-2">
                      <Button onClick={resetSimulation} variant="outline" size="sm" data-testid="try-again-button">
                        Try Different Details
                      </Button>
                      <Button onClick={onClose} size="sm" data-testid="close-simulator-button">
                        Close Simulator
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/eligibility-wizard.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel } from "@/components/ui/form";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Progress } from "@/components/ui/progress";
import { ArrowRight, ArrowLeft, CheckCircle } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { type Language, useTranslation } from "@/lib/i18n";

const formSchema = z.object({
  educationLevel: z.enum(["pre-matric", "post-matric"]),
  casteCategory: z.enum(["general", "obc", "sc", "st"]),
  incomeLevel: z.enum(["below-1-lakh", "1-2-lakh", "2-5-lakh", "above-5-lakh"]),
  state: z.string().min(1)
});

interface EligibilityWizardProps {
  isOpen: boolean;
  onClose: () => void;
  language: Language;
}

interface ScholarshipResult {
  name: string;
  amount: string;
  deadline: string;
  eligible: boolean;
}

export default function EligibilityWizard({ isOpen, onClose, language }: EligibilityWizardProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [results, setResults] = useState<ScholarshipResult[]>([]);
  const [showResults, setShowResults] = useState(false);
  const t = useTranslation(language);
  const { toast } = useToast();

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      educationLevel: "pre-matric",
      casteCategory: "general",
      incomeLevel: "below-1-lakh",
      state: ""
    }
  });

  const eligibilityMutation = useMutation({
    mutationFn: async (data: z.infer<typeof formSchema>) => {
      const response = await apiRequest('POST', '/api/check-eligibility', data);
      return response.json();
    },
    onSuccess: (data) => {
      setResults(data.scholarships || []);
      setShowResults(true);
      toast({
        title: "Eligibility Check Complete",
        description: `Found ${data.scholarships?.length || 0} eligible scholarships`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to check eligibility. Please try again.",
        variant: "destructive"
      });
    }
  });

  const onSubmit = (data: z.infer<typeof formSchema>) => {
    eligibilityMutation.mutate(data);
  };

  const nextStep = () => {
    if (currentStep < 4) setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    if (currentStep > 1) setCurrentStep(currentStep - 1);
  };

  const resetWizard = () => {
    setCurrentStep(1);
    setShowResults(false);
    setResults([]);
    form.reset();
  };

  const steps = [
    { number: 1, title: "Education Level" },
    { number: 2, title: "Category" },
    { number: 3, title: "Income" },
    { number: 4, title: "Location" }
  ];

  if (showResults) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto" data-testid="eligibility-results">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <CheckCircle className="text-secondary" />
              Eligibility Results
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            {results.length > 0 ? (
              results.map((scholarship, index) => (
                <div key={index} className="border border-border rounded-lg p-4 bg-card">
                  <h3 className="font-semibold text-lg mb-2">{scholarship.name}</h3>
                  <div className="grid md:grid-cols-2 gap-2 text-sm">
                    <div>
                      <span className="text-muted-foreground">Amount: </span>
                      <span className="font-medium text-accent">{scholarship.amount}</span>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Deadline: </span>
                      <span className="font-medium">{scholarship.deadline}</span>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <p className="text-muted-foreground">No scholarships found matching your criteria.</p>
                <p className="text-sm text-muted-foreground mt-2">
                  Try adjusting your selection or check back later for new opportunities.
                </p>
              </div>
            )}

            <div className="flex gap-2">
              <Button onClick={resetWizard} variant="outline" data-testid="restart-wizard">
                Check Again
              </Button>
              <Button onClick={onClose} data-testid="close-results">
                {t('close')}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl" data-testid="eligibility-wizard">
        <DialogHeader>
          <DialogTitle>Smart Eligibility Wizard</DialogTitle>
        </DialogHeader>

        {/* Progress Indicator */}
        <div className="space-y-4 mb-6">
          <Progress value={(currentStep / 4) * 100} className="h-2" />
          <div className="flex justify-between">
            {steps.map((step) => (
              <div key={step.number} className="flex flex-col items-center">
                <div 
                  className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold border-2 ${
                    currentStep >= step.number 
                      ? 'progress-step active' 
                      : 'border-border bg-background'
                  }`}
                >
                  {step.number}
                </div>
                <span className="text-xs mt-1 text-center">{step.title}</span>
              </div>
            ))}
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Step 1: Education Level */}
            {currentStep === 1 && (
              <FormField
                control={form.control}
                name="educationLevel"
                render={({ field }) => (
                  <FormItem data-testid="education-level-step">
                    <FormLabel className="text-xl font-semibold">What's your current education level?</FormLabel>
                    <FormControl>
                      <RadioGroup 
                        onValueChange={field.onChange} 
                        defaultValue={field.value}
                        className="grid md:grid-cols-2 gap-4"
                      >
                        <div className="flex items-center p-4 border border-border rounded-lg hover:bg-muted transition-colors">
                          <RadioGroupItem value="pre-matric" id="pre-matric" className="mr-3" />
                          <div>
                            <label htmlFor="pre-matric" className="font-medium cursor-pointer">Pre-Matric</label>
                            <div className="text-sm text-muted-foreground">Classes 1-10</div>
                          </div>
                        </div>
                        <div className="flex items-center p-4 border border-border rounded-lg hover:bg-muted transition-colors">
                          <RadioGroupItem value="post-matric" id="post-matric" className="mr-3" />
                          <div>
                            <label htmlFor="post-matric" className="font-medium cursor-pointer">Post-Matric</label>
                            <div className="text-sm text-muted-foreground">Class 11+</div>
                          </div>
                        </div>
                      </RadioGroup>
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {/* Step 2: Caste Category */}
            {currentStep === 2 && (
              <FormField
                control={form.control}
                name="casteCategory"
                render={({ field }) => (
                  <FormItem data-testid="caste-category-step">
                    <FormLabel className="text-xl font-semibold">Select your category</FormLabel>
                    <FormControl>
                      <RadioGroup 
                        onValueChange={field.onChange} 
                        defaultValue={field.value}
                        className="grid md:grid-cols-2 gap-4"
                      >
                        {[
                          { value: "general", label: "General" },
                          { value: "obc", label: "OBC (Other Backward Class)" },
                          { value: "sc", label: "SC (Scheduled Caste)" },
                          { value: "st", label: "ST (Scheduled Tribe)" }
                        ].map((option) => (
                          <div key={option.value} className="flex items-center p-4 border border-border rounded-lg hover:bg-muted transition-colors">
                            <RadioGroupItem value={option.value} id={option.value} className="mr-3" />
                            <label htmlFor={option.value} className="font-medium cursor-pointer">{option.label}</label>
                          </div>
                        ))}
                      </RadioGroup>
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {/* Step 3: Income Level */}
            {currentStep === 3 && (
              <FormField
                control={form.control}
                name="incomeLevel"
                render={({ field }) => (
                  <FormItem data-testid="income-level-step">
                    <FormLabel className="text-xl font-semibold">What's your family's annual income?</FormLabel>
                    <FormControl>
                      <RadioGroup 
                        onValueChange={field.onChange} 
                        defaultValue={field.value}
                        className="grid md:grid-cols-2 gap-4"
                      >
                        {[
                          { value: "below-1-lakh", label: "Below ‚Çπ1 Lakh" },
                          { value: "1-2-lakh", label: "‚Çπ1-2 Lakh" },
                          { value: "2-5-lakh", label: "‚Çπ2-5 Lakh" },
                          { value: "above-5-lakh", label: "Above ‚Çπ5 Lakh" }
                        ].map((option) => (
                          <div key={option.value} className="flex items-center p-4 border border-border rounded-lg hover:bg-muted transition-colors">
                            <RadioGroupItem value={option.value} id={option.value} className="mr-3" />
                            <label htmlFor={option.value} className="font-medium cursor-pointer">{option.label}</label>
                          </div>
                        ))}
                      </RadioGroup>
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {/* Step 4: State */}
            {currentStep === 4 && (
              <FormField
                control={form.control}
                name="state"
                render={({ field }) => (
                  <FormItem data-testid="state-step">
                    <FormLabel className="text-xl font-semibold">Select your state</FormLabel>
                    <FormControl>
                      <RadioGroup 
                        onValueChange={field.onChange} 
                        defaultValue={field.value}
                        className="grid md:grid-cols-2 gap-4"
                      >
                        {[
                          "Bihar", "Jharkhand", "West Bengal", "Uttar Pradesh", 
                          "Odisha", "Assam", "Other"
                        ].map((state) => (
                          <div key={state} className="flex items-center p-4 border border-border rounded-lg hover:bg-muted transition-colors">
                            <RadioGroupItem value={state.toLowerCase()} id={state} className="mr-3" />
                            <label htmlFor={state} className="font-medium cursor-pointer">{state}</label>
                          </div>
                        ))}
                      </RadioGroup>
                    </FormControl>
                  </FormItem>
                )}
              />
            )}

            {/* Navigation Buttons */}
            <div className="flex justify-between">
              <Button 
                type="button" 
                variant="outline" 
                onClick={prevStep}
                disabled={currentStep === 1}
                data-testid="wizard-prev-button"
              >
                <ArrowLeft className="mr-2" size={16} />
                {t('previousStep')}
              </Button>

              {currentStep < 4 ? (
                <Button 
                  type="button" 
                  onClick={nextStep}
                  data-testid="wizard-next-button"
                >
                  {t('nextStep')}
                  <ArrowRight className="ml-2" size={16} />
                </Button>
              ) : (
                <Button 
                  type="submit" 
                  disabled={eligibilityMutation.isPending}
                  data-testid="wizard-submit-button"
                >
                  {eligibilityMutation.isPending ? "Checking..." : "Check Eligibility"}
                </Button>
              )}
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/footer.tsx.content:
import { GraduationCap, Facebook, Twitter, Youtube, MessageSquare } from "lucide-react";
import { Button } from "@/components/ui/button";
import { type Language, useTranslation } from "@/lib/i18n";

interface FooterProps {
