import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Play, Eye, Share2, Clock, Globe } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { type Language, useTranslation } from "@/lib/i18n";

interface VideoSectionProps {
  language: Language;
}

interface Video {
  id: string;
  title: string;
  description: string;
  duration: string;
  views: string;
  languages: string;
  thumbnail: string;
  embedUrl?: string;
}

const videos: Video[] = [
  {
    id: "aadhaar-seeding-guide",
    title: "Complete Guide to Aadhaar Seeding",
    description: "Learn how to seed your bank account with Aadhaar for seamless DBT transfers and scholarship payments.",
